import './assets/css/base/base.css';
import './assets/css/componentes/card.css'
import Home  from "./pages/Home"
import Sobre from "./pages/Sobre"



function App() {



/*
  todo esto esto de aqui es de lo que se encarga el react router
  se encarga de las rutas del sitio
  en este ejemplo si voy y escribo en el buscador de chorome 
*/
  const location = window.location.pathname

  const router = () => {
    if(location === "/"){
      return <Home/>
    }else{
      return <Sobre/>
    }
  }

  return (
    <>
      {router}
    </>
  );
}

export default App;
